import React, { useState } from "react";
function Counter(props) {
  let [x, y] = useState(0);
  let plus = () => {
    y(x+1);
  };
  let minus = () => {
    y(x - 1);
  };
  let reset = () => {
    y(0);
  };
  return (
    <>
      <p>{x}</p>
      <button onClick={plus}>+</button>
      <button onClick={minus}>-</button>
      <button onClick={reset}>Reset</button>
    </>
  );
}
export default Counter;
