import React, { useState } from "react";
function Counter(props) {
  let [x, y] = useState(0);
  let plus = () => {
    y(x+1);
  };
  return (
    <>
      <button onMouseOver={plus}>Button clicked {x} times</button>
    </>
  );
  
}
export default Counter;
