import React, { Component } from "react";
import "./Button.css"
class Counter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }
  x = () => {
    this.setState({ count: this.state.count + 1 });
  }
  y = () => {
    this.setState({ count: this.state.count - 1 });
  }
  z = () => {
    this.setState({ count: 0 });
  }
  
  render() {
    return (
      <>
      <div class="count">
        <h1 style={{color:"maroon"}}>COUNTER APPLICATION</h1>
        </div>
        <div>{this.state.count}</div>
        <div class="oper">
        <button onMouseOver={this.x} style={{color:"green",padding:"30px 42px 20px 42px",hover:""}}>+</button>
        <button onMouseOver={this.y} style={{color:"green",padding:"30px 42px 20px 42px"}}>-</button>
        <button onMouseOver={this.z} style={{color:"green",padding:"30px 42px 20px 42px",fontSize:"bold"}}>Reset</button>
       
        </div>
      </>
    );
  }
}
export default Counter;
