import React, { Component } from "react";
class Counter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      Day:"",
      Date:""
    };
  }
  x = () => {
    this.setState({ count: this.state.count + 1 });
  }
  y = () => {
    this.setState({ count: this.state.count - 1 });
  }
  z = () => {
    this.setState({ count: 0 });
  }
  s=()=>{
    let ts=new Date()
    this.setState({day:this.state.toLocaleDateString})
  }
  p=()=>{
        this.setState({day:this.state.getDate})
  }
  render() {
    return (
      <>
        <div>{this.state.count}</div>
        <button onMouseOver={this.x}>+</button>
        <button onClick={this.y}>-</button>
        <button onClick={this.z}>Reset</button>
        <button onClick={this.s}>Day</button>
        <button onClick={this.p}>Date</button>
      </>
    );
  }
}

export default Counter;
